export const i18nNS = [
  "routes/next/organization/detail/about",
  "datasets/focuses",
];

export function hasGeneralInformation(organization: {
  bio: string | null;
  areas: Array<{ area: { slug: string; name: string } }>;
  focuses: Array<{ focus: { slug: string } }>;
  supportedBy: string[];
}) {
  return (
    organization.bio !== null ||
    organization.areas.length > 0 ||
    organization.focuses.length > 0 ||
    organization.supportedBy.length > 0
  );
}

export function hasContactOrSoMeInformation(organization: {
  email: string | null;
  phone: string | null;
  website: string | null;
  city: string | null;
  street: string | null;
  streetNumber: string | null;
  zipCode: string | null;
  facebook: string | null;
  linkedin: string | null;
  twitter: string | null;
  instagram: string | null;
  youtube: string | null;
  mastodon: string | null;
  tiktok: string | null;
  xing: string | null;
}) {
  return Object.values(organization).some((value) => value !== null);
}

export function hasContactInformation(organization: {
  email: string | null;
  phone: string | null;
  website: string | null;
  city: string | null;
  street: string | null;
  streetNumber: string | null;
  zipCode: string | null;
}) {
  return (
    organization.email !== null ||
    organization.phone !== null ||
    organization.website !== null ||
    hasAddress(organization)
  );
}

export function hasAddress(organization: {
  city: string | null;
  street: string | null;
  streetNumber: string | null;
  zipCode: string | null;
}) {
  return (
    organization.city !== null ||
    organization.street !== null ||
    organization.streetNumber !== null ||
    organization.zipCode !== null
  );
}

export function hasStreet(organization: { street: string | null }) {
  return organization.street !== null;
}

export function hasSocialService(organization: {
  facebook: string | null;
  linkedin: string | null;
  twitter: string | null;
  instagram: string | null;
  youtube: string | null;
  mastodon: string | null;
  tiktok: string | null;
  xing: string | null;
}) {
  return Object.values(organization).some((value) => value !== null);
}

